mapper.select:

    select id, ExamineRoom_Code, Course_Code, Course_SubCode, Examinee_Num,
     Examinee_Name, Score, Video, File, Creat_Time from exam_record
     order by Creat_Time desc limit #{startNum},#{endNum}

		select id, ExamineRoom_Code, Course_Code, Course_SubCode, Examinee_Num,
         Examinee_Name, Score, Video, File, Creat_Time from exam_record
		where Examinee_Num = #{examineeNum,jdbcType=VARCHAR}
			order by Creat_Time desc limit #{startNum},#{endNum}
